// Custom Scroll
$(function() {
	$('.sidebarNavScroll').slimScroll({
		height: "calc(100vh - 250px)",
		color: '#17202b',
		alwaysVisible: false,
		size: "5px",
		distance: '1px',
		railVisible: false,
		railColor: "#0066bf",
		position: 'left',
	});
});


